mapper.select:
select a.* , b.username , c.ordercode , d.goodsname from topic a , users b , orders c , goods d where a.usersid = b.usersid and a.ordersid = c.ordersid and
a.goodsid = d.goodsid

  and a.usersid = #{usersid}


  and a.ordersid = #{ordersid}


  and a.goodsid = #{goodsid}


  and a.num = #{num}


  and a.contents = #{contents}


  and a.addtime = #{addtime}
